import { useState,useEffect } from "react";
import axios from 'axios';
import { HuePicker} from "react-color";


const ImageCluster =()=>{

    const [initialImage,setInitialImage] = useState([]);
    const[imgURL,setImgURL] = useState("");
    const [color,setColor] = useState({
        hex:'#ff0500'
    })


    const uploadImage = () =>{
        console.log(initialImage)
        if (initialImage && initialImage.name) {
            const formData = new FormData();
            formData.append("image", initialImage);
            formData.append("color",color.rgb)
    
            axios.post('http://127.0.0.1:8000/cluster', formData, {
                headers: {
                    'Content-Type': 'multipart/form-data',
                }
            }).then((response) => console.log(response.data))
              .catch((error) => console.error("Upload error:", error));
        }
    }

    const updateImage = (e) =>{
        const file = e.target.files[0];
        setInitialImage(file);

    
        if (file) {
            const previewURL = URL.createObjectURL(file);
            setImgURL(previewURL);
        }
    }

    useEffect(() => {
        return () => {
            if (imgURL) {
                URL.revokeObjectURL(imgURL);
            }
        };
    }, [imgURL]);

    return(
    <div>
        <input type="file" accept="image/png,image/jpeg"  onChange={(e)=>updateImage(e)}/>
        <button onClick={()=>console.log(initialImage)}>Upload</button>
        {initialImage && initialImage.name ?
        <div>
            <HuePicker     
        color={color}
        onChange={updatedColor => {setColor(updatedColor);console.log(color)}}
    />
    <div style={{ border:`5px solid ${color.hex}` }}>
        <img src={imgURL ? imgURL : null}/>
    </div>
        </div>
        
        :
        <p>No image</p>
        }

        
    </div>
    )
}
export default ImageCluster;
