import { useState,useEffect } from "react";
import axios from 'axios';
import { HuePicker     } from "react-color";


const ImageCluster =()=>{

    const [initialImage,setInitialImage] = useState([]);
    const[imgURL,setImgURL] = useState("");
    const [segmentedImage,setSegmentedImage] = useState([]);
    const [elbowGraph, setElbowGraph] = useState([]);
    const [binaryMask, setBinaryMask] = useState([]);
    const [ripleysK, setRipleysK] = useState([]);
    const [accumulationIndex, setAccumulationIndex] = useState(0);

    const [imageResults,setImageResults] = useState([]);
    const [color,setColor] = useState('#fff')


    const uploadImage = () =>{
        console.log(initialImage)
        if (initialImage && initialImage.name) {
            const formData = new FormData();
            formData.append("image", initialImage);
    
            axios.post('http://127.0.0.1:8000/cluster', formData, {
                headers: {
                    'Content-Type': 'multipart/form-data',
                }
            }).then((response) => console.log(response.data))
              .catch((error) => console.error("Upload error:", error));
        }
    }

    const updateImage = (e) =>{
        setInitialImage(e.target.files[0]);
       
        console.log(initialImage)
    }

    return(
    <div>
        <input type="file" accept="image/png,image/jpeg"  onChange={(e)=>updateImage(e)}/>
        <button onClick={uploadImage}>Upload</button>

        <HuePicker     
            color={color}
            onChange={updatedColor => {setColor(updatedColor)}}
        />
        <div style={{ border:`5px solid ${color.hex}` }}>
            <img src={imgURL ? imgURL : null}/>
        </div>
    </div>
    )
}
export default ImageCluster;
