import { useState, useEffect } from "react";
import axios from 'axios';
import {Col, Container, Row} from 'react-bootstrap';
import '../styles/imgIntensity.css'
import {FileUploader} from "react-drag-drop-files";



const ImageCluster = () => {

    const fileTypes = ["JPG","PNG","JPEG"];
    const [file,setFile] = useState(null);
    
    const [imgURL, setImgURL] = useState("");
    const[cluster,setCluster] = useState(1);
    const [images,setImages] = useState([]);



    const uploadImage = () => {
        
        if (file && file.name) {
            const formData = new FormData();
            formData.append("clusters",cluster)
            formData.append("image", file);
            for (const pair of formData.entries()) {
                console.log(pair[0], pair[1]);
              }
            axios.post('http://127.0.0.1:8000/cluster', formData, {
                headers: {
                    'Content-Type': 'multipart/form-data',
                }
            }).then((response) => {setImages(response.data);console.log(response.data)})
                .catch((error) => console.error("Upload error:", error));
        }
    }

    const updateImage = (file) => {
        setImages([]);
        setFile(file);
        if (file) {
            const previewURL = URL.createObjectURL(file);
            setImgURL(previewURL);
        }
    }

    useEffect(() => {
        return () => {
            if (imgURL) {
                URL.revokeObjectURL(imgURL);
            }
        };
    }, [imgURL]);

    return (
        <Container className="img-intensity-container">
            <Row>
             <Col className="img-upload" align="center">
                <FileUploader handleChange={updateImage} name="file" types={fileTypes}/>
             </Col>
            </Row>
            <Row>
                <Col className="img-container">
                    {file && file.name ?
                        <img className="file-uploaded" src={imgURL ? imgURL : null} width='300px' height='300px' />
                        :
                        <p className="no-image">No image</p>
                    }
                </Col>
            </Row>
            <Row>
                <Col className="cluster-number">
                <div className="number-input">
                    <button className={cluster == 1 ? 'disabled':''} onClick={()=>cluster > 1 ? setCluster(cluster-1):''}>-</button>
                    <p>{cluster}</p>
                    <button  className={cluster ==5 ? 'disabled':''} onClick={()=>cluster < 5 ? setCluster(cluster+1):''}>+</button>
                </div>
                <button  className="analyze" onClick={ uploadImage}>Upload</button>
                </Col>
            </Row>
            <Row>
            {images.length>0 ? (
                images.map((image)=>
                    <Col className="img-container">
                    <p>{image.name}</p>
                    <img src={image.image_base64} width='300px' height='300px'/>
                    <p>{image.value}</p>
                    </Col> 
                )
            ):(
                <></>
            )}
            </Row>
        </Container>
    )
}
export default ImageCluster;
