import { Row,Container,Col } from "react-bootstrap";
import "../styles/toolGuide.css"


const ToolGuide = () =>{

    const dummyProps = {
        toolDescription: "Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua. Ut enim ad minim veniam, quis nostrud exercitation ullamco laboris nisi ut aliquip ex ea commodo consequat. Duis aute irure dolor in reprehenderit in voluptate velit esse cillum dolore eu fugiat nulla pariatur. Excepteur sint occaecat cupidatat non proident, sunt in culpa qui officia deserunt mollit anim id est laborum.",
        toolImage:"",
        steps:[
            {
                briefDescription:"Select your image",
                image:"",
                deepDescription:"Its best to load a microscopy image"   
            },
            {
                briefDescription:"Select your image",
                image:"",
                deepDescription:"Its best to load a microscopy image"   
            },
            {
                briefDescription:"Select your image",
                image:"",
                deepDescription:"Its best to load a microscopy image"   
            }
        ],
    }
    return(
        <Container>
            <h1 className="header">What this tool does</h1>
            <Row>
                <Col className="tool-desc-box">
                <img src={dummyProps.toolImage} />
                <p>{dummyProps.toolDescription}</p>
                </Col>
            </Row>
            <Row>
                {dummyProps.steps.map((step,index)=>(
                    <Col className="tool-step-box">
                        <h3>{index+1}. {step.briefDescription}</h3>
                        <img src={step.image}/>
                        <p>{step.deepDescription}</p>
                    </Col>
                ))}
            </Row>
        </Container>

    )
}
export default ToolGuide;
